require "sinatra/base"

IMAGES = [
  {title: "Image", url:"data:image/jpeg;base64,"}, 
  {title: "Maria Callas", url:"http://upload.wikimedia.org/wikipedia/commons/thumb/1/10/Maria_Callas_%28La_Traviata%29_2.JPG/220px-Maria_Callas_%28La_Traviata%29_2.JPG"}, 
  {title: "Dap Block", url:"http://www.hdwallpapersview.com/wp-content/uploads/2013/11/02/Funny-Humor-21.jpg"}

]

class App < Sinatra::Base
  get '/images' do
    @images = IMAGES  
    erb :images
  end

  get "/images/:index" do |index|
    @images = IMAGES[index]
  end

  get "/hello/:first_name?/?:last_name?" do |f, l|
    "Hello #{f} #{l}"
  end

  get "/" do
    erb :index
  end

  post "/form" do
    "Now you're inside a post request"
  end

end